// src/App.js
import React, { useEffect, useState } from 'react';
import './App.css';

const App = () => {
  const [userData, setUserData] = useState(null);
  const [showDialPad, setShowDialPad] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState('');

  useEffect(() => {
    fetch('https://randomuser.me/api/?page=1&results=1&seed=abc')
      .then((response) => response.json())
      .then((data) => setUserData(data.results[0]))
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  const handleContactClick = () => {
    setShowDialPad(true);
    setPhoneNumber(userData.phone);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-500 to-indigo-700">
      {userData && (
        <div className="max-w-md w-full mx-auto bg-white rounded-lg overflow-hidden shadow-md">
          <img
            className="w-full h-64 object-cover object-center"
            src={userData.picture.large}
            alt="User"
          />
          <div className="p-6 bg-gray-100">
            <h2 className="text-3xl font-bold text-purple-800 mb-2">{`${userData.name.title} ${userData.name.first} ${userData.name.last}`}</h2>
            <p className="text-gray-600 mb-4">{userData.email}</p>
            <div className="bg-purple-200 p-4 rounded-md mb-4">
              <p className="text-gray-800">{`${userData.location.street.number} ${userData.location.street.name}, ${userData.location.city}, ${userData.location.state}, ${userData.location.country} ${userData.location.postcode}`}</p>
            </div>
            <p className="text-gray-600 mb-4">{`Date of Birth: ${userData.dob.date}, Age: ${userData.dob.age}`}</p>
            <p className="text-gray-600 mb-4">{`Phone: ${userData.phone}, Cell: ${userData.cell}`}</p>
            {showDialPad ? (
              <div className="flex justify-center">
                <p className="text-lg font-bold mb-2">Dial Pad</p>
                {/* Simulate dial pad with an alert */}
                <button
                  className="bg-green-500 text-white py-2 px-4 rounded-md hover:bg-green-700 focus:outline-none focus:ring focus:border-green-300 ml-2"
                  onClick={() => alert(`Dial pad will open for ${phoneNumber}`)}
                >
                  Dial
                </button>
              </div>
            ) : (
              <div className="flex justify-center">
                <button
                  className="bg-purple-500 text-white py-2 px-4 rounded-md hover:bg-purple-700 focus:outline-none focus:ring focus:border-purple-300"
                  onClick={handleContactClick}
                >
                  Contact
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
