import React, { useEffect, useState } from 'react';
import './App.css';

const App = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    fetch('https://randomuser.me/api/?page=1&results=1&seed=abc')
      .then((response) => response.json())
      .then((data) => setUserData(data.results[0]))
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  return (
    <div className="App">
      {userData && (
        <div className="max-w-md mx-auto mt-10 bg-white rounded-md overflow-hidden shadow-md">
          <img
            className="w-full h-48 object-cover object-center"
            src={userData.picture.large}
            alt="User"
          />
          <div className="p-6">
            <h2 className="text-xl font-bold">{`${userData.name.title} ${userData.name.first} ${userData.name.last}`}</h2>
            <p className="text-gray-600">{userData.email}</p>
            <p className="text-gray-600">{`${userData.location.street.number} ${userData.location.street.name}, ${userData.location.city}, ${userData.location.state}, ${userData.location.country} ${userData.location.postcode}`}</p>
            <p className="text-gray-600">{`Date of Birth: ${userData.dob.date}, Age: ${userData.dob.age}`}</p>
            <p className="text-gray-600">{`Phone: ${userData.phone}, Cell: ${userData.cell}`}</p>
            {/* Display additional details as needed */}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
